import React, { useState } from "react";
import Grid from "@mui/material/Grid";
import { Dialog } from "primereact/dialog";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import MKBox from "components/MKBox";
import MKInput from "components/MKInput";
import MKButton from "components/MKButton";
import MKTypography from "components/MKTypography";
import "primeflex/primeflex.css";
import bgImage from "assets/images/illustrations/illustration-reset.jpg";
import checkUrl from "../../../redux/actions/checkAction"; // Sửa chính tả nếu cần

import CircularProgress from "@mui/material/CircularProgress"; // Import CircularProgress

const ContactUs = (props) => {
  const [inputValue, setInputValue] = useState("");
  const [visible, setVisible] = useState(false);

  const closeDialog = () => setVisible(false);

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  // Thay đổi handleCheck thành async function
  const handleCheck = async () => {
    try {
      await props.check(inputValue); // Đợi hàm check hoàn tất
      console.log(props?.data);
      setVisible(props.show); // Sau khi hoàn tất, mới setVisible
    } catch (error) {
      console.error("Error during check:", error);
    }
  };

  return (
    <>
      <Grid container spacing={3} alignItems="center">
        <Grid item xs={12} lg={6}>
          <MKBox
            display={{ xs: "none", lg: "flex" }}
            width="calc(100% - 2rem)"
            height="calc(100vh - 2rem)"
            borderRadius="lg"
            ml={2}
            mt={2}
            sx={{ backgroundImage: `url(${bgImage})` }}
          />
        </Grid>
        <Grid
          item
          xs={12}
          sm={10}
          md={7}
          lg={6}
          xl={4}
          ml={{ xs: "auto", lg: 6 }}
          mr={{ xs: "auto", lg: 6 }}
        >
          <MKBox
            bgColor="white"
            borderRadius="xl"
            shadow="lg"
            display="flex"
            flexDirection="column"
            justifyContent="center"
            mt={{ xs: 20, sm: 18, md: 20 }}
            mb={{ xs: 20, sm: 18, md: 20 }}
            mx={3}
          >
            <MKBox
              variant="gradient"
              bgColor="info"
              coloredShadow="info"
              borderRadius="lg"
              p={2}
              mx={2}
              mt={-3}
            >
              <MKTypography variant="h3" color="white">
                Check information
              </MKTypography>
            </MKBox>
            <MKBox p={3}>
              <MKTypography variant="body2" color="text" mb={3}>
                This website supports verifying whether information is real or fake
              </MKTypography>
              <form action="" autoComplete="off">
                <Grid container spacing={3}>
                  <Grid item xs={12} md={12}>
                    <MKInput
                      type="text"
                      variant="standard"
                      label="Enter the information to check"
                      InputLabelProps={{ shrink: true }}
                      fullWidth
                      value={inputValue}
                      onChange={handleChange}
                    />
                  </Grid>
                </Grid>
                <Grid container item justifyContent="center" xs={12} mt={5} mb={2}>
                  <MKButton type="button" variant="gradient" color="info" onClick={handleCheck}>
                    Check
                  </MKButton>
                </Grid>
              </form>
            </MKBox>
          </MKBox>
        </Grid>
      </Grid>
      <Dialog
        visible={props.isLoading} // Sử dụng props.isLoading để hiển thị dialog
        header="Loading..."
        modal
        onHide={closeDialog}
        style={{
          width: "300px",
          height: "150px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          backgroundColor: "rgba(255, 255, 255, 0.9)",
          border: "none",
          borderRadius: "8px",
        }}
        className="loading-dialog"
      >
        <div style={{ textAlign: "center" }}>
          <CircularProgress /> {/* Optional: Replace this with your own loading indicator */}
          <p style={{ marginTop: "10px" }}>Please wait...</p>
        </div>
      </Dialog>
      <Dialog
        header={
          <div
            style={{
              textTransform: "uppercase" /* Chữ in hoa */,
              fontWeight: "bold" /* Chữ đậm */,
              fontSize: "40px" /* Cỡ chữ 18px */,
            }}
          >
            {props?.data?.answer} NEWS
          </div>
        }
        visible={visible}
        onHide={closeDialog}
        style={{
          width: "1200px",
          height: "800px",
          backgroundColor: "#ffffff",
          border: "1px solid #000",
          textAlign: "center",
          padding: "14px",
        }}
        modal
      >
        <div
          className="card"
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            height: "100%",
          }}
        >
          <div
            className="card"
            style={{
              width: "100%",
              textAlign: "center",
              backgroundColor: "#e0e0e0",
              paddingTop: "20px",
              paddingBottom: "20px",
              paddingLeft: "80px",
              paddingRight: "80px",
              border: "1px solid #000",
            }}
          >
            <h1 style={{ fontSize: "30px" }}>{props?.data?.fact_check?.[0]?.[1]}</h1>
          </div>

          {/* Chỉnh lại phần chứa từ khóa và kết quả fact-check hiển thị theo hàng dọc */}
          <div
            className="card"
            style={{
              display: "flex",
              flexDirection: "column", // Sử dụng column để xếp chồng các phần tử theo chiều dọc
              width: "100%",
              margin: "20px 0",
            }}
          >
            {/* Từ khóa */}
            <div
              className="card"
              style={{
                width: "100%", // Sử dụng 100% chiều rộng
                height: "auto", // Cho phép chiều cao tự động
                backgroundColor: "#e0e0e0",
                border: "1px solid #000",
                marginBottom: "20px", // Thêm khoảng cách dưới để tạo khoảng cách giữa các phần
                display: "flex",
                flexDirection: "column",
              }}
            >
              <label
                style={{
                  fontWeight: "bold",
                  marginBottom: "10px",
                  padding: "10px",
                  borderBottom: "1px solid #000",
                  backgroundColor: "black",
                  color: "white",
                }}
              >
                Từ khóa
              </label>
              <div
                style={{
                  display: "flex",
                  flexDirection: "row", // Hiển thị các mục theo hàng ngang
                  justifyContent: "space-between", // Cách đều các mục trong hàng
                  overflowY: "auto",
                  width: "100%",
                }}
              >
                {props?.data?.fact_check?.[1]?.[1]?.map((item, index) => (
                  <div
                    key={index}
                    style={{
                      padding: "10px",
                      borderBottom: "1px solid #000",
                      width: "calc(100% / 3 - 20px)", // Chia đều chiều rộng cho mỗi mục, trừ đi khoảng cách padding
                      boxSizing: "border-box", // Đảm bảo padding không làm ảnh hưởng tới chiều rộng
                    }}
                  >
                    {item}
                  </div>
                ))}
              </div>
            </div>

            {/* Kết quả fact-check */}
            <div
              className="card"
              style={{
                width: "100%", // Sử dụng 100% chiều rộng
                padding: "20px",
                backgroundColor: "#f9f9f9",
                border: "1px solid #ccc",
                borderRadius: "8px",
                boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
                overflowY: "auto",
