              }}
            >
              <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
                {props?.data?.fact_check?.[2]?.[0]}
              </h2>
              {props?.data?.fact_check?.[2]?.slice(1)?.map((fact, index) => (
                <div
                  key={index}
                  style={{
                    marginBottom: "20px",
                    padding: "15px",
                    backgroundColor: "#fff",
                    borderRadius: "8px",
                    border: "1px solid #ddd",
                  }}
                >
                  <p style={{ fontWeight: "bold", marginBottom: "10px" }}>{fact?.[0]}</p>
                  <p style={{ marginBottom: "10px" }}>
                    <strong>{fact[1]}</strong> {fact?.[2]}
                  </p>
                  <p style={{ marginBottom: "10px" }}>
                    <strong>{fact[3]}</strong> {fact?.[4]}
                  </p>
                  <a
                    href={fact[5]}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{ color: "#007bff", textDecoration: "underline" }}
                  >
                    Link to Fact-Check
                  </a>
                </div>
              ))}
            </div>
          </div>

          <div
            className="card"
            style={{
              width: "100%",
              height: "150px",
              backgroundColor: "#e0e0e0",
              border: "1px solid #000",
            }}
          ></div>
        </div>
      </Dialog>
    </>
  );
};

ContactUs.propTypes = {
  check: PropTypes.func.isRequired,
  isLoading: PropTypes.bool.isRequired,
  show: PropTypes.bool.isRequired,
  data: PropTypes.object,
};

const mapStateToProps = (state) => ({
  isLoading: state.check.isLoading,
  data: state.check.data,
  show: state?.check?.show,
});

const mapDispatchToProps = (dispatch) => ({
  check: (url) => dispatch(checkUrl(url)), // Sửa từ chechUrl thành checkUrl
});

export default connect(mapStateToProps, mapDispatchToProps)(ContactUs);
